      if (readingListNotFinished.length === 0) {
        return (
          <div>
            <h3>Nothing to read? <button onClick={() => navigate('/books')}>Start adding books!</button></h3>
          </div>
        )
      }
      if (readingListNotFinished.length > 0) {
        return (
          <div>
            { readingListNotFinished }
          </div>
        ) 
      }
    }
  }


  const reviewCards = () => {
    if (myReviews) {
      return myReviews.map(review => <UserReviewCard key={ review.id } review={ review } locations={ locations } updateReview={ updateReview } loggedIn={ loggedIn } />)
    }
  }

  return (
    <div>
      <div id="user-greeting">
        <div id="greeting">
          <h1>Hi, { currentUser.username }! Here are your saved locations: </h1>
        </div>
      </div>
      <br/>
      <div>
        { myList }
      </div>
      <div id="reviews">
        <h1>Your Reviews:</h1>
        { reviewCards() }
      </div>
    </div>

  )
}

export default UserPage;
